package week1.day2;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=-1, maxNumber = 10; 
				
		if (maxNumber>0) 
		{
			 
		  for (i=1;i<=maxNumber;i++)
		   {
			  System.out.println ("The entered value is positive:"+ i);
		  	}
		
		}
		else
		{
			System.out.println ("The entered value is negative:"+ i);
		}
		

	}

}
